package com.coderhouse;

public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float numero = 1.65f;

		int numeroEntero = (int) numero;
		float porcionFlotante = numero - numeroEntero;

		System.out.println("Numero: " + numero + " - Parte entera: " + numeroEntero + " - Parte decimal:  " + porcionFlotante);

	}

}
